sys_variable_value.value:
(function execute(inputs, outputs) {

  if (inputs.response_body) {
    const result = JSON.parse(inputs.response_body);
    const translation = result.translations[0];
    outputs.detected_source_language = translation.detected_source_language;
    outputs.text = translation.text;
  }

})(inputs, outputs);

