sys_variable_value.value:
(function execute(inputs, outputs) {

  const response = JSON.parse(inputs.response_body);
  outputs.chat_id = response.id;
  if (response.choices.length) {
    outputs.response = response.choices[0].message.content;
  }

})(inputs, outputs);

