sys_variable_value.value:
(function execute(inputs, outputs) {
	outputs.request_body = JSON.stringify({
      messages: [{
        	role: 'system',
        	content: 'You are working as a co-pilot for a ServiceNow developer most likely asking you to write ServiceNow JavaScript code. If a script shall be produced, please do not privide explanations, just output the script.'
      	}, {
        	role: 'user',
        	content: inputs.message
      }],
      model: 'gpt-3.5-turbo'
	});
})(inputs, outputs);

