sys_script_include.script:
/* global global, sn_fd, Class, gs */
/* eslint no-undef: "error" */
var GPTUtil = Class.create();
GPTUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	prompt: function () {
		const result = {
			status: 'error',
			message: ''
		};

		const chatID = this.getParameter('chat_id');
		const prompt = this.getParameter('prompt');
		if (prompt) {
			try {
				const outputs = sn_fd.FlowAPI.getRunner()
					.action('x_424426_devops.prompt_gpt')
					.inForeground()
					.withInputs({ 
						'message': prompt
					})
					.run()
					.getOutputs();

				result.response = outputs.response;
				result.status = 'success';
			} catch (e) {
				result.status = 'error';
				result.message = e.toString();

				if (gs.hasRole('admin') && e.stack) {
					result.message += '\nStack: ' + e.stack;
				}
			}
		} else {
			result.message = gs.getMessage('devops_invalid_parameters');
		}

		return JSON.stringify(result);
	},

    type: 'GPTUtil'
});
