sys_ui_script.script:
var x_424426_devops = x_424426_devops || {};
x_424426_devops.config = (function() {
	"use strict";

	/* eslint no-undef: "error" */
	if (x_424426_devops.config) {
		return x_424426_devops.config;
	}

	var $window, g_user;
	x_424426_devops.loader.get('window', function (_window) {
		loadConfig($window = _window, g_user);
	});

	x_424426_devops.loader.get('g_user', function (_g_user) {
		loadConfig($window, g_user = _g_user);
	});

	function loadConfig($window, g_user) {
		if ($window && g_user && g_user.hasRoleExactly('x_424426_devops.devops_user')) {
			var ga = new $window.GlideAjax('ContextMenuClient').setScope('x_424426_devops');
			ga.addParam('sysparm_name', 'getConfig');
			ga.getXMLAnswer(function (result) {
				result = JSON.parse(result);
				if (result.status == 'success') {
					delete result.status;
					x_424426_devops.loader.set('config', result);
				}
			});
		}
	}

	return {
		type: 'config'
	};
})();
