sys_ui_script.script:
var x_424426_devops = x_424426_devops || {};
x_424426_devops.portal_lang_select = (function() {
	"use strict";
	/* global setTimeout */
	/* eslint no-undef: "error" */
	if (x_424426_devops.portal_lang_select) {
		return x_424426_devops.portal_lang_select;
	}

	var ootbLangSelect = '4ec9343d29b1c510f8779f284c7771c0';

	setTimeout(function () {
		var $window = this;

		var observer = new $window.MutationObserver(function (mutations) {
			for (var i = 0; i < mutations.length; i++) {
				var mutation = mutations[i];
				if (mutation.addedNodes) {
					for (var j = 0; j < mutation.addedNodes.length; j++) {
						var node = mutation.addedNodes[j];
						if (node.classList && node.classList.contains('v' + ootbLangSelect)) {
							if (!node.classList.contains('devops-lang-injected')) {
								node.classList.add('devops-lang-injected');
								new DevOpsLangInject($window, node);
							}
						}
					}
				}
			}   
		});

		observer.observe($window.document, {
			childList: true,
			subtree: true
		});
	});

	return {
		type: 'portal_lang_select'
	};

	function DevOpsLangInject($window, node) {
		const $scope = $window.angular.element(node).scope();

		// only show if at least one other language than system language is available
		const ga = new $window.GlideAjax('ConfigUtilClient').setScope('x_424426_devops');
		ga.addParam('sysparm_name', 'getConfig');
		ga.getXMLAnswer(function (result) {
			result = JSON.parse(result);
			if (result && result.status == 'success') {
				if (result.languages.length > 1) {
					$scope.$evalAsync(function () {
						$scope.data.enableLangSelector = true;
					});
				}
			}
		});
	}
})();
