sys_ui_script.script:
var x_424426_devops = x_424426_devops || {};
x_424426_devops.text_capture = (function() {
	"use strict";

	/* eslint no-undef: "error" */
	var textCapture = {
		text: '',
		type: 'text_capture'
	};

	x_424426_devops.loader.get('window', function ($window) {
		$window.document.addEventListener('click', function () {
			var text = getSelectionText($window);
			if (text) {
				textCapture.text = text; 
			}
		});
	});

	return textCapture;

	// source: https://stackoverflow.com/a/5379408
	function getSelectionText($window) {
		var text = "";
		var activeEl = $window.activeElement;
		var activeElTagName = activeEl ? activeEl.tagName.toLowerCase() : null;
		if (
			(activeElTagName == "textarea") || (activeElTagName == "input" &&
												/^(?:text|search|password|tel|url)$/i.test(activeEl.type)) &&
			(typeof activeEl.selectionStart == "number")
		) {
			text = activeEl.value.slice(activeEl.selectionStart, activeEl.selectionEnd);
		} else if ($window.getSelection) {
			text = $window.getSelection().toString();
		}
		return text;
	}
})();
