sys_ui_script.script:
var x_424426_devops = x_424426_devops || {};
x_424426_devops.text_capture = (function() {
	"use strict";

	/* global GlideModal */
	/* eslint no-undef: "error" */
	var textCapture = {
		text: '',
		type: 'text_capture'
	};

	var $window = null;
	var g_user = null;

	x_424426_devops.loader.get('window', function (_window) {
		$window = _window;
		injectSearch($window, g_user);
	});

	x_424426_devops.loader.get('g_user', function (_g_user) {
		g_user = _g_user;
		injectSearch($window, g_user);
	});

	function injectSearch($window, g_user) {
		if ($window && g_user && g_user.hasRoleExactly('x_424426_devops.devops_user')) {
			$window.document.addEventListener('click', function () {
				var text = getSelectionText($window);
				if (text) {
					textCapture.text = text; 
				}
			});

			var isMac = $window.navigator.userAgent.indexOf('Mac OS X') > -1;
			$window.document.addEventListener('keydown', function (event) {
				// do not open the code search dialog if either in studio or there is already a code search window opened
				if ($window.isDevStudio || $window.location.href.indexOf('x_424426_devops_code_search') > -1) {
					return;
				}

				if ((isMac ? event.metaKey : event.ctrlKey) && event.shiftKey && (event.key == 'f' || event.key == 'F')) {
					event.preventDefault();

					var gm = new GlideModal('devops_codesearch', false, 'modal-95');
					gm.setTitle("Code Search");
					gm.on('beforeclose', function () {

					});

					var iframe = '<iframe src="/x_424426_devops_code_search.do';
					if (textCapture.text) {
						iframe += '?search=' + encodeURIComponent(textCapture.text);
					}

					iframe += '" style="width: 100%; height: 650px; border: 0; overflow: hidden;" />';
					gm.renderWithContent(iframe);
				}
			});
		}
	}

	return textCapture;

	// source: https://stackoverflow.com/a/5379408
	function getSelectionText($window) {
		var text = "";
		var activeEl = $window.activeElement;
		var activeElTagName = activeEl ? activeEl.tagName.toLowerCase() : null;
		if (
			(activeElTagName == "textarea") || (activeElTagName == "input" &&
												/^(?:text|search|password|tel|url)$/i.test(activeEl.type)) &&
			(typeof activeEl.selectionStart == "number")
		) {
			text = activeEl.value.slice(activeEl.selectionStart, activeEl.selectionEnd);
		} else if ($window.getSelection) {
			text = $window.getSelection().toString();
		}
		return text;
	}
})();
