x_424426_devops_context_menu_item.client_script:
(function () {
	var x = window.event.screenX - 500;
	var y = window.event.screenY - 250;
	var bgs = window.open('sys.scripts.do', 'DevOps+ Background Script', 'width=1000,height=550,top=' + y + ',left=' + x);
	bgs.addEventListener('DOMContentLoaded', function () {
		var script = 'var recordGr = new GlideRecord(\'' + g_form.getTableName() + '\');'
			+ '\nrecordGr.addQuery(\'sys_id\', \'' + g_form.getUniqueValue() + '\');'
			+ '\nrecordGr.setLimit(1);'
			+ '\nrecordGr.query();'
			+ '\nif (recordGr.next()) {'
			+ '\n  recordGr.${FIELD} = ' + JSON.stringify(g_form.getValue('${FIELD}')) + ';'
			+ '\n  recordGr.setWorkflow(false);'
			+ '\n  recordGr.update();'
			+ '\n}'
		;

		var cursorPos = script.indexOf('recordGr.${FIELD}') + 'recordGr.'.length;
		var scriptInput = bgs.document.getElementById('runscript');
		scriptInput.value = script;
		scriptInput.setSelectionRange(cursorPos, cursorPos);	
		bgs.document.querySelector('select[name="sys_scope"]').value = 'global';
	});
})();
